function history_insert(&$error_message = '') {
	global $Translation;

	// mm: can member insert record?
	$arrPerm = getTablePermissions('history');
	if(!$arrPerm['insert']) return false;

	$data = [
		'id' => Request::val('id', ''),
		'email' => Request::val('email', ''),
		'password' => Request::val('password', ''),
		'type' => Request::val('type', ''),
		'firstname' => Request::val('firstname', ''),
		'lastname' => Request::val('lastname', ''),
		'username' => Request::val('username', ''),
		'address' => Request::val('address', ''),
		'country' => Request::val('country', 'Kenya'),
		'contact_info' => Request::val('contact_info', ''),
		'contact_verify' => Request::val('contact_verify', '0'),
		'photo' => Request::val('photo', ''),
		'status' => Request::val('status', ''),
		'activate_code' => Request::val('activate_code', ''),
		'reset_code' => Request::val('reset_code', ''),
		'created_on' => Request::val('created_on', ''),
		'source' => Request::val('source', ''),
		'verified' => Request::val('verified', ''),
		'occupation' => Request::val('occupation', 'Investor'),
		'company' => Request::val('company', ''),
		'company_site' => Request::val('company_site', ''),
		'language' => Request::val('language', ''),
		'time_zone' => Request::val('time_zone', ''),
		'currency' => Request::val('currency', ''),
		'email_comm' => Request::val('email_comm', ''),
		'phone_comm' => Request::val('phone_comm', ''),
		'marketing' => Request::val('marketing', ''),
		'two_auth' => Request::val('two_auth', '0'),
		'two_auth_secret' => Request::val('two_auth_secret', ''),
		'g_id' => Request::val('g_id', ''),
		'f_id' => Request::val('f_id', ''),
		't_id' => Request::val('t_id', ''),
		'timestamp' => Request::val('timestamp', 'current_timestamp()'),
		'change_part' => Request::val('change_part', ''),
	];


	// hook: history_before_insert
	if(function_exists('history_before_insert')) {
		$args = [];
		if(!history_before_insert($data, getMemberInfo(), $args)) {
			if(isset($args['error_message'])) $error_message = $args['error_message'];
			return false;
		}
	}

	$error = '';
	// set empty fields to NULL
	$data = array_map(function($v) { return ($v === '' ? NULL : $v); }, $data);
	insert('history', backtick_keys_once($data), $error);
	if($error) {
		$error_message = $error;
		return false;
	}

	$recID = db_insert_id(db_link());

	update_calc_fields('history', $recID, calculated_fields()['history']);

	// hook: history_after_insert
	if(function_exists('history_after_insert')) {
		$res = sql("SELECT * FROM `history` WHERE `hist_id`='" . makeSafe($recID, false) . "' LIMIT 1", $eo);
		if($row = db_fetch_assoc($res)) {
			$data = array_map('makeSafe', $row);
		}
		$data['selectedID'] = makeSafe($recID, false);
		$args = [];
		if(!history_after_insert($data, getMemberInfo(), $args)) { return $recID; }
	}

	// mm: save ownership data
	set_record_owner('history', $recID, getLoggedMemberID());

	// if this record is a copy of another record, copy children if applicable
	if(strlen(Request::val('SelectedID'))) history_copy_children($recID, Request::val('SelectedID'));

	return $recID;
}

function history_copy_children($destination_id, $source_id) {
	global $Translation;
	$requests = []; // array of curl handlers for launching insert requests
	$eo = ['silentErrors' => true];
	$safe_sid = makeSafe($source_id);

	// launch requests, asynchronously
	curl_batch($requests);
}

function history_delete($selected_id, $AllowDeleteOfParents = false, $skipChecks = false) {
	// insure referential integrity ...
	global $Translation;
	$selected_id = makeSafe($selected_id);

	// mm: can member delete record?
	if(!check_record_permission('history', $selected_id, 'delete')) {
		return $Translation['You don\'t have enough permissions to delete this record'];
	}

	// hook: history_before_delete
	if(function_exists('history_before_delete')) {
		$args = [];
		if(!history_before_delete($selected_id, $skipChecks, getMemberInfo(), $args))
			return $Translation['Couldn\'t delete this record'] . (
				!empty($args['error_message']) ?
					'<div class="text-bold">' . strip_tags($args['error_message']) . '</div>'
					: '' 
			);
	}

	sql("DELETE FROM `history` WHERE `hist_id`='{$selected_id}'", $eo);

	// hook: history_after_delete
	if(function_exists('history_after_delete')) {
		$args = [];
		history_after_delete($selected_id, getMemberInfo(), $args);
	}

	// mm: delete ownership data
	sql("DELETE FROM `membership_userrecords` WHERE `tableName`='history' AND `pkValue`='{$selected_id}'", $eo);
}

function history_update(&$selected_id, &$error_message = '') {
	global $Translation;

	// mm: can member edit record?
	if(!check_record_permission('history', $selected_id, 'edit')) return false;

	$data = [
		'id' => Request::val('id', ''),
		'email' => Request::val('email', ''),
		'password' => Request::val('password', ''),
		'type' => Request::val('type', ''),
